import sys
print("<div id=\"center\"><div id=\"content\"><br><br><br><div id=\"container\"></div><p style=\"color:white;text-align:center;font-family: opnsans;font-size:30px;\" id=\"txt\">File: %s</p></div></div><script src=\"ruffle.js\"></script><script>window.RufflePlayer=window.RufflePlayer||{};window.addEventListener(\"load\",(event)=>{const ruffle=window.RufflePlayer.newest();const player=ruffle.createPlayer();const container=document.getElementById(\"container\");container.appendChild(player);player.load(\"%s\");});</script><style>@font-face {font-family: opnsans;src: url(opnsans.ttf);}body{background-color: #000000;}#content {width: 37%;margin: 0 auto;}</style>" % (sys.argv[1],sys.argv[1]))
